mapper.select:
SELECT r.ID,r.USER_ID,r.RECIPIENT,r.PHONE,r.EXPRESS_NUM,r.EXPRESS_COMPANY,r.BILL_TYPE,r.BILL_HEAD,r.MONEY,r.STATUS, r.ADDRESS,r.TIME,r.ADD_TIME,r.VALID_REMARK,u.USERNAME,u.PHONE as USERPHONE FROM bus_user_receipt r,bus_user u WHERE r.USER_ID = u.ID
AND r.STATUS = #{status}
AND (u.USERNAME LIKE concat('%',#{keyword},'%' ) OR u.PHONE LIKE concat('%',#{keyword},'%' ) OR r.RECIPIENT LIKE concat('%',#{keyword},'%' ) OR r.PHONE LIKE concat('%',#{keyword},'%' ) OR r.EXPRESS_NUM LIKE concat('%',#{keyword},'%' ))
AND r.ADD_TIME >= #{startdate}
AND r.ADD_TIME <= #{enddate}
ORDER BY r.ADD_TIME DESC
