mapper.select:
	select 

  from pob_charging_temp_station
  where 1=1

 	and BUS_TYPE = #{busType,jdbcType=SMALLINT} 


  and BUS_MEC = #{busMec,jdbcType=SMALLINT} 


 	and VALID_STATUS = #{validStatus,jdbcType=SMALLINT} 


 	and VALID_STATUS not in

	#{item}  




  and STATION_NAME like concat('%',#{searchStationName,jdbcType=VARCHAR},'%' )

  order by UPDATE_TIME desc

SELECT t.ID, t.REAL_STATION_ID, t.STATION_NAME, t.BUS_TYPE, t.UPDATE_TIME, t.REQUEST_TYPE, t.VALID_STATUS, t.VALID_TIME, t.VALID_REMARK FROM pob_charging_temp_station t WHERE t.VALID_STATUS < 5
and t.VALID_STATUS = #{validStatus}
AND t.UPDATE_TIME >= #{startDate}
AND t.UPDATE_TIME <= #{endDate}
AND t.STATION_NAME like concat('%',#{stationName},'%' )
order by t.UPDATE_TIME desc
